mapper.update:
update emp2 set name=#{name,jdbcType=VARCHAR},
				deptno=#{deptno, jdbcType=VARCHAR}
where empno=#{empno}

mapper.insert:
insert into emp2(empno, name, birthday, deptno, emp_type, tel, hobby, pay, position, pempno)
values(#{empno}, #{name,jdbcType=VARCHAR}, sysdate, #{deptno,jdbcType=VARCHAR}, #{emp_type,jdbcType=VARCHAR}
, #{tel,jdbcType=VARCHAR}, #{hobby,jdbcType=VARCHAR}, #{pay}, #{position,jdbcType=VARCHAR}, #{pempno} )
